package eu.webeid.example.security;

import eu.webeid.security.authtoken.WebEidAuthToken;
import jakarta.servlet.http.HttpServletRequest;
import jakarta.servlet.http.HttpServletResponse;
import org.junit.jupiter.api.Test;
import org.mockito.ArgumentCaptor;
import org.mockito.Mockito;
import org.springframework.http.HttpHeaders;
import org.springframework.http.HttpMethod;
import org.springframework.http.MediaType;
import org.springframework.security.authentication.AuthenticationManager;
import org.springframework.security.web.authentication.preauth.PreAuthenticatedAuthenticationToken;

import java.io.BufferedReader;
import java.io.StringReader;

import static org.assertj.core.api.Assertions.assertThat;
import static org.junit.jupiter.api.Assertions.assertDoesNotThrow;
import static org.mockito.Mockito.mock;
import static org.mockito.Mockito.when;

class WebEidAjaxLoginProcessingFilterTest {

    private static final String AUTH_TOKEN =
        "{\"algorithm\":\"ES384\"," +
            "\"unverifiedCertificate\":\"\"," +
            "\"issuerApp\":\"https://web-eid.eu/web-eid-app/releases/2.0.0+976\"," +
            "\"signature\":\"Z+r6IIx0lmXNTHrlJOTknVaOYszXba5ko1e8rjWJXd4nzIVsxeTps/Revg+tuKREkkIuIKhwvOnARdWV6vBmhjlpRUZn9aNPSDRP98T/0sPZgDp31hwsWfCAYnPzzYC9\"," +
            "\"format\":\"web-eid:1\"}";

    @Test
    void testAttemptAuthentication() throws Exception {
        final HttpServletRequest request = mock(HttpServletRequest.class);
        final HttpServletResponse response = mock(HttpServletResponse.class);
        when(request.getMethod()).thenReturn(HttpMethod.POST.name());
        when(request.getContentType()).thenReturn(MediaType.APPLICATION_JSON_VALUE);
        when(request.getHeader(HttpHeaders.CONTENT_TYPE)).thenReturn(MediaType.APPLICATION_JSON_VALUE);
        when(request.getReader()).thenReturn(new BufferedReader(new StringReader(AUTH_TOKEN)));

        final AuthenticationManager authenticationManager = mock(AuthenticationManager.class);

        assertDoesNotThrow(() ->
            new WebEidAjaxLoginProcessingFilter("/auth/login", authenticationManager)
                .attemptAuthentication(request, response));
        ArgumentCaptor<PreAuthenticatedAuthenticationToken> captor = ArgumentCaptor.forClass(PreAuthenticatedAuthenticationToken.class);
        Mockito.verify(authenticationManager).authenticate(captor.capture());
        PreAuthenticatedAuthenticationToken token = captor.getValue();
        assertThat(token.getCredentials()).isExactlyInstanceOf(WebEidAuthToken.class);
        WebEidAuthToken webEidAuthToken = (WebEidAuthToken) token.getCredentials();
        assertThat(webEidAuthToken.getAlgorithm()).isEqualTo("ES384");
        assertThat(webEidAuthToken.getUnverifiedCertificate()).isEqualTo("");
        assertThat(webEidAuthToken.getSignature()).isEqualTo("Z+r6IIx0lmXNTHrlJOTknVaOYszXba5ko1e8rjWJXd4nzIVsxeTps/Revg+tuKREkkIuIKhwvOnARdWV6vBmhjlpRUZn9aNPSDRP98T/0sPZgDp31hwsWfCAYnPzzYC9");
        assertThat(webEidAuthToken.getFormat()).isEqualTo("web-eid:1");
    }

}
